mapper.select:
SELECT p.*, ca.type_category_name, dc.d_category_name, uc.u_category_name, img.p_img_url FROM product p LEFT JOIN type_category ca ON p.type_category_id = ca.type_category_id LEFT JOIN d_category dc ON p.d_category_id = dc.d_category_id LEFT JOIN u_category uc ON dc.u_category_id = uc.u_category_id LEFT JOIN ( SELECT product_id, p_img_url FROM ( SELECT product_id, p_img_url, ROW_NUMBER() OVER (PARTITION BY product_id ORDER BY p_img_seq ASC) AS rn FROM p_img ) sub WHERE rn = 1 ) img ON p.product_id = img.product_id WHERE p.delete_check = 0
AND p.store_id = #{storeId}
AND (
p.type_category_id IN
#{id}
OR p.d_category_id IN
#{id}
OR uc.u_category_id IS NOT NULL AND uc.u_category_id IN
#{id}
)
ORDER BY p.product_date DESC
ORDER BY p.sale_cnt DESC
ORDER BY p.product_name ASC
ORDER BY p.product_date DESC
